import _ from "lodash";

export function getSum(transaction, type) {
  let sum = _(transaction)
    .groupBy("type")
    .map((obj, key) => {
      if (!type) return _.sumBy(obj);
      return {
        'type': key,
        'color': obj[0].color,
        'total': _.sumBy(obj, "amount"),
      };
    })
    .value();
  console.log(sum);
}
export function getLabels() {}
