import React from "react";
import { Chart, ArcElement } from "chart.js";
import { Doughnut } from "react-chartjs-2";
import Labels from "./Labels";

const Graph = () => {
  Chart.register(ArcElement);

  const config = {
    data: {
      datasets: [
        {
          data: [300, 50, 100],
          backgroundColor: [
            "rgb(255, 99, 132)",
            "rgb(54, 162, 235)",
            "rgb(255, 205, 86)",
          ],
          hoverOffset: 4,
          borderRadius: 30,
          spacing: 10,
        },
      ],
    },
    options: {
      cutout: 115,
    },
  };

  return (
    <div className="flex justify-content max-w-xs mx-auto">
      <div className="item">
        <div className="chart relative">
          <Doughnut {...config} />
          <p className="title" style={{ fontSize: "30px", fontWeight: 700 }}>
            Total
            <span
              style={{ display: "block", color: "#50C878", fontSize: "30px" }}
            >
              ${0}
            </span>
          </p>
        </div>
        <div className="flex flex-col py-10 gap-4">{/* Labels */}
            <Labels />
        </div>
      </div>
    </div>
  );
};

export default Graph;
